mapper.insert:
INSERT INTO note (
	note_idx, title, text, img, titleimg,
	create_at, edit_at, view_count,
	content_idx, genre_idx, category_idx, userpg_idx
)
VALUES (
	#{noteIdx}, #{title}, #{text}, #{img}, #{titleimg},
	SYSDATE, SYSDATE, 0,
	#{contentIdx}, #{genreIdx}, #{categoryIdx}, #{userpgIdx}
)

mapper.update:
UPDATE
	note
SET
	title = #{title}
	text = #{text}
	img = #{img}
	titleimg = #{titleimg}
	edit_at = SYSDATE
	content_idx = #{contentIdx}
	genre_idx = #{genreIdx}
	category_idx = #{categoryIdx}
WHERE
	note_idx = #{noteIdx}
